import Table from "./table/table";
import Login from "./login/login";
import React, { useState } from "react";
import { QuizData } from "./quiz/quizData.js";

function App() {
  const [userAnswers, setUserAnswers] = useState(
    Array(QuizData.length).fill("")
  );
  const [score, setScore] = useState(0);

  const handleSubmit = () => {
    // Calculate the score by comparing userAnswers with actual answers
    let userScore = 0;
    QuizData.forEach((item, index) => {
      if (userAnswers[index].toLowerCase() === item.answer.toLowerCase()) {
        userScore++;
      }
    });
    setScore(userScore);
  };

  return (
    <div className="App">
      <h1>Quiz App</h1>
      {QuizData.map((item, index) => (
        <div key={index}>
          <p>{item.question}</p>
          <input
            type="text"
            value={userAnswers[index]}
            onChange={(e) => {
              const newAnswers = [...userAnswers];
              newAnswers[index] = e.target.value;
              setUserAnswers(newAnswers);
            }}
          />
        </div>
      ))}
      <button onClick={handleSubmit}>Submit</button>
      {score !== 0 && <p>Your Score: {score}</p>}
    </div>
  );
}

export default App;
