import Table from "./table/table";
import Login from "./login/login";
import React, { useState } from "react";
import quizData from "./quiz/quizData";

function App() {
  const [answer, setAnswer] = useState(Array(quizData.length).fill(""));

  const changeEvent = (e) => {
    setAnswer({ ...answer, [e.target.name]: e.target.value });
  };
  const display = () => {
    console.log(answer);
  };
  return (
    <div className="w-1/2 bg-slate-100 flex-col justify-center m-auto mt-8 rounded-md p-6">
      <div className="flex justify-center">
        <h1 className="text-blue-400 uppercase font-serif my-4 ">Quiz App</h1>
      </div>
      {quizData.map((item, index) => (
        <div key={index}>
          <p>{item.question}</p>
          <input
            className=" bg-gray-50 border border-blue-400 text-blue-400  text-base rounded-md"
            type="text"
            value={answer.answer}
            onChange={changeEvent}
          />
        </div>
      ))}
      <div className="flex justify-center flex-col items-center">
        <button
          className="bg-blue-500 text-white p-2 rounded-md my-4 w-full"
          onClick={display}
        >
          Submit
        </button>
      </div>
    </div>
  );
}

export default App;
