import React, { useState } from "react";

export default function SecondQuiz() {
  const [number, setNumber] = useState(1);
  const [inputValues, setInputValues] = useState([""]);
  const onChange = (e) => {
    setNumber(e.target.value);
  };
  const handleEvent = (event, index) => {
    const newInput = [...inputValues];
    newInput[index] = event.target.value;
    setInputValues(newInput);
  };
  const submit = () => {
    const num = parseInt(number, 10);
    if (!isNaN(num) && num > 0) {
      setNumber(num);
      setInputValues(new Array(num).fill(""));
      console.log("hgsdhsgdhs");
    }
  };
  return (
    <div className="w-1/2 bg-slate-100 flex-col justify-center m-auto mt-8 rounded-md p-6">
      <div className="flex  justify-center">
        <h1 className=" text-blue-500 text-lg">Quiz App</h1>
      </div>
      <div className="flex justify-center mt-8">
        <input
          className=" bg-gray-50 border border-blue-400 text-blue-400  text-base rounded-md"
          type="number"
          value={number}
          onChange={onChange}
        />
        <button
          className="bg-blue-500 text-white text-base ml-4 rounded-md p-1"
          onClick={submit}
        >
          Enter No
        </button>
      </div>
      <div>
        {inputValues.map((value, index) => (
          <div className="flex flex-col justify-center" key={index}>
            <input
              className=" bg-gray-50 border border-blue-400 text-blue-400  text-base rounded-md mt-2"
              type="number"
              value={value}
              onChange={(event) => handleEvent(event, index)}
            />
          </div>
        ))}
      </div>
    </div>
  );
}
