import React, { useState } from "react";

export default function SecondQuiz() {
  const [numbers, setNumbers] = useState([""]);

  const handleInputChange = (event, index) => {
    const newNumbers = [...numbers];
    newNumbers[index] = event.target.value;
    setNumbers(newNumbers);
  };

  const addInputField = () => {
    setNumbers([...numbers, ""]);
  };

  const showNumbers = () => {
    console.log("Entered Numbers:", numbers);
  };

  return (
    <div>
      <h1>Number Input</h1>
      {numbers.map((number, index) => (
        <div key={index}>
          <label htmlFor={`numberInput${index}`}>Enter a Number:</label>
          <input
            type="number"
            id={`numberInput${index}`}
            value={number}
            onChange={(event) => handleInputChange(event, index)}
          />
        </div>
      ))}
      <button onClick={addInputField}>Add Input Field</button>
      <button onClick={showNumbers}>Show Numbers</button>
    </div>
  );
}
