import React, { useCallback } from "react";
import ReactFlow, {
  addEdge,
  MiniMap,
  Controls,
  Background,
  useNodes,
  useEdges,
} from "react-flow";
import "react-flow/dist/style.css";
import { v4 as uuidv4 } from "uuid";

const generateRandomNodeId = () => `node-${uuidv4()}`;

const ReactDraganddrop = () => {
  const initialNodes = [
    {
      id: generateRandomNodeId(),
      type: "input",
      data: { label: "Input Node" },
      position: { x: 50, y: 50 },
    },
    {
      id: generateRandomNodeId(),
      type: "default",
      data: { label: "Node 2" },
      position: { x: 200, y: 100 },
    },
  ];

  const initialEdges = [
    {
      id: "edge-1",
      source: initialNodes[0].id,
      target: initialNodes[1].id,
      arrowHeadType: "arrowclosed",
      animated: true,
      label: "Edge Label",
      style: { stroke: "#FF5733", strokeWidth: 2 },
    },
  ];

  const [nodes, setNodes] = useNodes(initialNodes);
  const [edges, setEdges] = useEdges(initialEdges);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  return (
    <ReactFlow
      elements={nodes.concat(edges)}
      onConnect={onConnect}
      style={{ width: "100%", height: "500px" }}
      minZoom={0.5}
      maxZoom={2}
    >
      <MiniMap
        nodeStrokeColor={(n) => {
          if (n.style?.background) return n.style.background;
          if (n.type === "input") return "#0041d0";
          if (n.type === "output") return "#ff0072";
          if (n.type === "default") return "#1a192b";
          return "#eee";
        }}
        nodeColor={(n) => {
          if (n.style?.background) return n.style.background;
          return "#fff";
        }}
        nodeBorderRadius={2}
      />
      <Controls />
      <Background color="#aaa" gap={16} />
    </ReactFlow>
  );
};

export default ReactDraganddrop;
