import React, { useState, useCallback } from "react";
import ReactFlow, {
  addEdge,
  MiniMap,
  Controls,
  Background,
  removeElements,
  useNodes,
  useEdges,
} from "react-flow";
import "react-flow-renderer/dist/style.css";
import Modal from "react-modal";

Modal.setAppElement("#root");

const initialElements = [
  {
    id: "1",
    type: "rectangle",
    data: { label: "Rectangle Node" },
    position: { x: 100, y: 100 },
  },
  {
    id: "2",
    type: "triangle",
    data: { label: "Triangle Node" },
    position: { x: 300, y: 100 },
  },
];

const OverviewFlow = () => {
  const [elements, setElements] = useState(initialElements);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedNode, setSelectedNode] = useState(null);

  const onConnect = useCallback(
    (params) => {
      setElements((prevElements) => addEdge(params, prevElements));
    },
    [setElements]
  );

  const onEdgeMouseEnter = (edge, event) => {
    // Show delete option when hovering over an edge
    event.target.style.cursor = "pointer";
  };

  const deleteEdge = (edgeId) => {
    setElements((prevElements) =>
      removeElements([{ id: edgeId }], prevElements)
    );
  };

  const copyElements = () => {
    // Copy selected elements and paste them on the canvas
    const copiedElements = elements.filter((el) =>
      selectedNode ? el.id === selectedNode : false
    );
    setElements((prevElements) => [...prevElements, ...copiedElements]);
  };

  const onElementClick = (event, element) => {
    setSelectedNode(element.id);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const addRandomNode = () => {
    // Generate a random node and add it to the canvas
    const randomId = Math.random().toString(36).substring(7);
    const newNode = {
      id: randomId,
      type: "default",
      data: { label: randomId },
      position: { x: 0, y: 0 },
      style: {
        background: "#" + ((Math.random() * 0xffffff) << 0).toString(16),
      },
    };
    setElements((prevElements) => [...prevElements, newNode]);
  };

  return (
    <div style={{ height: "100vh" }}>
      <ReactFlow
        elements={elements}
        onConnect={onConnect}
        nodeTypes={{
          rectangle: RectangleNode,
          triangle: TriangleNode,
          default: DefaultNode,
        }}
        edgeTypes={{ customEdge: CustomEdge }}
        onElementClick={onElementClick}
        onEdgeMouseEnter={onEdgeMouseEnter}
      >
        <MiniMap nodeStrokeColor={(n) => n.style?.background || "#eee"} />
        <Controls />
        <Background color="#aaa" gap={16} />
      </ReactFlow>
      <button onClick={addRandomNode}>Add Random Node</button>
      <button onClick={copyElements}>Copy Selected Node</button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Node Details"
      >
        {selectedNode && (
          <div>
            <h2>Node Details for {selectedNode}</h2>
            {/* Add your node details and different modals here */}
            {selectedNode === "1" ? (
              <RectangleModal />
            ) : selectedNode === "2" ? (
              <TriangleModal />
            ) : (
              <DefaultModal />
            )}
            <button onClick={closeModal}>Close</button>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default OverviewFlow;
