import Table from "./table/table";
import Login from "./login/login";
import React, { useState } from 'react';
//import './App.css';
import QuizQuestion from "./QuizQuestion ";

function App() {


  const questions = [
    {
      question: 'Question 1: What is 2 + 2?',
      correctAnswer: '4',
    },
    {
      question: 'Question 2: What is the capital of France?',
      correctAnswer: 'Paris',
    },
    {
      question: 'Question 3: What is the capital of France?',
      correctAnswer: 'Paris',
    },
    {
      question: 'Question 4: What is the capital of France?',
      correctAnswer: 'Paris',
    },
    // Add more questions here
  ];

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);

  const handleAnswerSubmit = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="App">
      <h1>Quiz App</h1>
      {currentQuestion ? (
        <QuizQuestion
          question={currentQuestion.question}
          correctAnswer={currentQuestion.correctAnswer}
          onAnswerSubmit={handleAnswerSubmit}
        />
      ) : (
        <p>Quiz Completed! Your score: {score}</p>
      )}
    </div>
  );
}

export default App;
