import Table from "./table/table";
import Login from "./login/login";
import React, { useState } from "react";
import quizData from "./quiz/quizData";

function App() {
  const [userAnswers, setUserAnswers] = useState(
    Array(quizData.length).fill("")
  );
  const [score, setScore] = useState(0);
  const [resultsVisible, setResultsVisible] = useState(false);

  const handleSubmit = () => {
    // Calculate the score by comparing userAnswers with actual answers
    let userScore = 0;
    quizData.forEach((item, index) => {
      if (userAnswers[index].toLowerCase() === item.answer.toLowerCase()) {
        userScore++;
      }
    });
    setScore(userScore);
    setResultsVisible(true);
  };

  return (
    <div className="w-1/2 bg-slate-100 flex-col justify-center m-auto mt-8 rounded-md p-6">
      <div className="flex justify-center">
        <h1 className="text-blue-400 uppercase font-serif my-6 ">Quiz App</h1>
      </div>
      {quizData.map((item, index) => (
        <div key={index}>
          <p>{item.question}</p>
          <input
            type="text"
            value={userAnswers[index]}
            onChange={(e) => {
              const newAnswers = [...userAnswers];
              newAnswers[index] = e.target.value;
              setUserAnswers(newAnswers);
            }}
          />
          {resultsVisible && (
            <p
              className={
                userAnswers[index].toLowerCase() === item.answer.toLowerCase()
                  ? "correct"
                  : "incorrect"
              }
            >
              {userAnswers[index].toLowerCase() === item.answer.toLowerCase()
                ? "Correct"
                : "Incorrect"}
            </p>
          )}
        </div>
      ))}
      <button onClick={handleSubmit} className="bg-blue-500 text-white p-4">
        Submit
      </button>
      {resultsVisible && <p>Your Score: {score}</p>}
    </div>
  );
}

export default App;
